package java2_002;

public class instanceof1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="서호석";
		System.out.println("name의 값"+name);
		
		System.out.println("name이 String 맞는지  "+(name  instanceof String));   //형이 맞는지 안맞는지 확인, instanceof
	}

}
